
public class Autoboxing_9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Character ch = 'x';
		char c = ch;
		System.out.println(c);
		
		Integer itg = 1;
		int i = itg;
		System.out.println(i);
		
		Boolean bool = true;
		boolean b = bool;
		System.out.println(b);
		
		Double dou = 1.1;
		double d = dou;
		System.out.println(d);
	}

}
